void processRacingState(){

    VDP_setTextPlane(WINDOW); // set the text plane so that text never gets covered

    JOY_setEventHandler(&racingJoyHandler);

    PAL_setPalette(PAL0, pal_2.data, DMA); // set raceway pal
    PAL_setPalette(PAL3, img_tucker_wins.palette->data, DMA); // set HUD pal

    u8 ind = TILE_USER_INDEX; // create starting index
    VDP_loadTileSet(&tlst, TILE_USER_INDEX, DMA);



    PLR_init(&player_1, 148, 44, &spr_dirtbiker, currentMap->mapDef, BG_B, PAL1, &ind); // initialize player structures with the proper overworld map structure, sprite resources, background layer, and palette
    PLR_init(&player_2, 148, 156, &spr_dirtbiker, currentMap->mapDef, BG_A, PAL2, &ind);
    ind += tlst.numTile;

    PLR_setVRAMTileIndex(&player_1, 0); // manually set VRAM tile indexes for sprites so that copy sprites can use the same slot in VRAM
    PLR_setVRAMTileIndex(&player_2, 24);

    player_1_copy = SPR_addSpriteEx(&spr_dirtbiker, 0, 105, TILE_ATTR_FULL(PAL1, 0, 0, 0, TILE_SPRITE_INDEX), SPR_FLAG_AUTO_VISIBILITY); // set sprite location in VRAM
    player_2_copy = SPR_addSpriteEx(&spr_dirtbiker, 0, 0, TILE_ATTR_FULL(PAL2, 0, 0, 0, TILE_SPRITE_INDEX + 24), SPR_FLAG_AUTO_VISIBILITY); // these sprites are copies of a player that appear on the other player's screen


    VDP_clearPlane(WINDOW, DMA); // make vertical splitscreen
    VDP_setWindowVPos (false, 14);

    VDP_fillTileMapRect(WINDOW,TILE_ATTR_FULL(PAL0,TRUE,FALSE,FALSE,15),0,12,40,18); // adds splitscreen border



    PLR_setMapPos(&player_1, currentMap->startPosX, currentMap->startPosY); // place each player at the starting line (player 2 is to the right of player 1)
    PLR_setMapPos(&player_2, currentMap->startPosX + 24, currentMap->startPosY);



    while(gameState == RACING_STATE)    { // do the stuff needed when the race is going on

        MAP_scrollTo(player_1.mp, player_1.offset_x, player_1.offset_y);

        MAP_scrollTo(player_2.mp, player_2.offset_x, player_2.offset_y - 112);


        PLR_moveOnMap(&player_1, currentMap);
        PLR_moveOnMap(&player_2, currentMap);

        if (PLR_playerScreensOverlap(&player_1, &player_2)) { // if players are on each others screens, draw player copies in the corresponding locations.

            if (player_1.pos_y + player_2.offset_y - player_1.offset_y <= 92)   { // this prevents player 2's copy from overlapping onto the bottom screen
                SPR_setPosition(player_2_copy, player_1.pos_x + player_2.offset_x - player_1.offset_x, player_1.pos_y + player_2.offset_y - player_1.offset_y);
            }
            SPR_setPosition(player_1_copy, player_2.pos_x + player_1.offset_x - player_2.offset_x, player_2.pos_y + player_1.offset_y - player_2.offset_y);

        }   else if (player_1_copy->x != -30)    { // if players are off screen, move them to a single offscreen position instead of constantly updating their position.

            SPR_setPosition(player_1_copy, -30, -30);
            SPR_setPosition(player_2_copy, -30, -30);

        }


        if ((player_1.vel_x == 0 && player_1.vel_y == 0) && (player_2.vel_x == 0 && player_2.vel_y == 0))   { // to check if players are moving. If they are, loop the driving sound effect. Otherwise, stop it.
            // stop action is handled right at the end of racingJoyHandler so stopPlayPCM isn't called every frame
        }   else    {
            if (XGM_isPlayingPCM(SOUND_PCM_CH2_MSK) == false)   { // if the sound effect stopped playing, loop it.
                XGM_startPlayPCM(SFX_DRIVING, 0, SOUND_PCM_CH2);
            }
        }

        PLR_incrementTimers(&player_1); // increment any timers the players may have
        PLR_incrementTimers(&player_2);

        if (player_1.laps == 3) { // if tucker wins
            VDP_drawImageEx(WINDOW, &img_tucker_wins, TILE_ATTR_FULL(PAL3, 0, 0, 0, ind), 14, 12, false, DMA);
            JOY_setEventHandler(&nullJoyHandler);
            XGM_startPlayPCM(SFX_VICTORY, 1, SOUND_PCM_CH2); // start victory sound effect, with it having priority over driving sound effect
            wait(200);
            gameState = TITLE_STATE;
        }
        if (player_2.laps == 3) {
            VDP_drawImageEx(WINDOW, &img_oakley_wins, TILE_ATTR_FULL(PAL3, 0, 0, 0, ind), 14, 12, false, DMA);
            JOY_setEventHandler(&nullJoyHandler);
            XGM_startPlayPCM(SFX_VICTORY, 1, SOUND_PCM_CH2); // start victory sound effect, with it having priority over driving sound effect
            wait(200);
            gameState = TITLE_STATE;
        }

        SPR_update();
        SYS_doVBlankProcess();
    }
    // cleanup


    PLR_release(&player_1);
    PLR_release(&player_2);
    SPR_releaseSprite(player_1_copy);
    SPR_releaseSprite(player_2_copy);

    VDP_setWindowVPos (true, 28);

    VDP_setVerticalScroll(BG_B, 0);
    VDP_setHorizontalScroll(BG_B, 0);
    VDP_setVerticalScroll(BG_A, 0);
    VDP_setHorizontalScroll(BG_A, 0);



    VDP_clearPlane(WINDOW, DMA);
    VDP_clearPlane(BG_A, DMA);
    VDP_clearPlane(BG_B, DMA);
    SPR_update();
}

void processTitleState(){

    JOY_setEventHandler(&titleJoyHandler);

    VDP_loadTileSet(img_title_screen.tileset,TILE_USER_INDEX,DMA);
    PAL_setPalette(PAL0, img_title_screen.palette->data, DMA);
    VDP_drawImageEx(BG_B, &img_title_screen, TILE_ATTR_FULL(PAL0, 0, 0, 0, TILE_USER_INDEX), 0, 0, false, DMA);


    VDP_setTextPlane(BG_A);
    VDP_setTextPalette(PAL3);
    PAL_setColor(63, RGB24_TO_VDPCOLOR(0x0090ce));// set PAL3's last color to the proper color for text display

    VDP_drawText(">Have a race", 2, 20);
    VDP_drawText("Customize bikes", 3, 22);
    VDP_drawText("View credits", 3, 24);

    titleMenuChoice = 0; // reset title menu choice so it aligns with "have a race"

    while (gameState == TITLE_STATE)    {

        SYS_doVBlankProcess();

    }

    VDP_clearTextArea(0, 0, 40, 28);
    VDP_clearPlane(BG_B, DMA);
}

void processPlayerCustomizeState()  {

    JOY_setEventHandler(&playerCustomizeJoyHandler);

    VDP_loadTileSet(img_customization_screen.tileset,TILE_USER_INDEX,DMA);
    PAL_setPalette(PAL0, img_customization_screen.palette->data, DMA);
    VDP_drawImageEx(BG_B, &img_customization_screen, TILE_ATTR_FULL(PAL0, 0, 0, 0, TILE_USER_INDEX), 0, 0, false, DMA);

    customize_tucker = SPR_addSprite(&spr_customize_tucker, 40, 90, TILE_ATTR(PAL1, 0, 0, 0));
    customize_oakley = SPR_addSprite(&spr_customize_oakley, 200, 90, TILE_ATTR(PAL2, 0, 0, 0));

    while (gameState == PLAYER_CUSTOMIZE_STATE) {

        SPR_update();
        SYS_doVBlankProcess();

    }
    //cleanup

    SPR_releaseSprite(customize_tucker);
    SPR_releaseSprite(customize_oakley);

    VDP_clearPlane(BG_B, DMA);


    SPR_update();
}

void processMapSelectionState() {

    JOY_setEventHandler(&mapSelectionJoyHandler);

    mapSelectionMenuChoice = 0; // reset menu

    VDP_drawText("Select your racetrack!", 9, 0);
    VDP_drawText("<-", 1, 5);
    VDP_drawText("->", 37, 5);
    VDP_drawText(mapNames[mapSelectionMenuChoice], 20 - strlen(mapNames[mapSelectionMenuChoice])/2, 5); // place name of raceway in the center of the screen

    while (gameState == MAP_SELECTION_STATE)    {

        SYS_doVBlankProcess();
    }


    VDP_clearTextArea(0, 0, 40, 28);
}

void processCreditsState()  {

    JOY_setEventHandler(&creditsJoyHandler);

    VDP_drawText("Created By - Jack Swallowell", 2, 3);
    VDP_drawText("Audio - Pixabay & Roblox", 2, 5);
    VDP_drawText("Made For - Tucker & Oakley Mckeegan", 2, 7);
    VDP_drawText("Playtesting - Jake & Rose Swallowell", 2, 9);
    VDP_drawText("Moral Support - Fluffer the cat", 2, 11);
    VDP_drawText("Immoral Support - Nugget the cat", 2, 13);
    VDP_drawText("Special Thanks - Joe Kuhn", 2, 15);
    VDP_drawText("Written and compiled in SGDK", 2, 26);

    while(gameState == CREDITS_STATE)   {

        SYS_doVBlankProcess();
    }

    VDP_clearTextArea(0, 0, 40, 28);
}

int main()  {

    SPR_init();
    JOY_init();

    XGM_setPCM(SFX_YIPPEE, sfx_yippee, sizeof(sfx_yippee));
    XGM_setPCM(SFX_DRIVING, sfx_driving, sizeof(sfx_driving));
    XGM_setPCM(SFX_BOING, sfx_boing, sizeof(sfx_boing));
    XGM_setPCM(SFX_VICTORY, sfx_victory, sizeof(sfx_victory));
    XGM_setPCM(SFX_CHANGE_MENU, sfx_change_menu, sizeof(sfx_change_menu));
    XGM_setPCM(SFX_SELECT_MENU, sfx_select_menu, sizeof(sfx_select_menu));
    XGM_setPCM(SFX_PAINT_SPLASH, sfx_paint_splash, sizeof(sfx_paint_splash));

    PAL_setPalette(PAL1, spr_dirtbiker.palette->data, DMA); // player 1 will use PAL1, player 2 will use PAL2
    PAL_setPalette(PAL2, spr_dirtbiker.palette->data, DMA);

    PAL_setColor(17, player_bike_colors_2[player_1_color_indeces[0]]); //  setting player 1 color according to color indeces
    PAL_setColor(24, player_bike_colors_1[player_1_color_indeces[0]]); //

    PAL_setColor(33, player_bike_colors_2[player_2_color_indeces[0]]); //  setting player 2 color according to color indeces
    PAL_setColor(40, player_bike_colors_1[player_2_color_indeces[0]]); //

    raceway_02_map = OVW_MAP_create(84, 70, 466, 400, RACEWAY_02_COLLISION, &raceway_02);
    raceway_03_map = OVW_MAP_create(59, 56, 252, 224, RACEWAY_03_COLLISION, &raceway_03);
    raceway_04_map = OVW_MAP_create(124, 84, 160, 360, RACEWAY_04_COLLISION, &raceway_04);
    raceway_05_map = OVW_MAP_create(94, 84, 392, 384, RACEWAY_05_COLLISION, &raceway_05);

    maps[0] = raceway_02_map;
    maps[1] = raceway_03_map;
    maps[2] = raceway_04_map;
    maps[3] = raceway_05_map;

    currentMap = raceway_02_map;


    while(true)    {

        if (gameState == TITLE_STATE)   {
            processTitleState();
        }
        if (gameState == PLAYER_CUSTOMIZE_STATE)   {
            processPlayerCustomizeState();
        }
        if (gameState == MAP_SELECTION_STATE)   {
            processMapSelectionState();
        }
        if (gameState == RACING_STATE)  {
            processRacingState();
        }
        if (gameState == CREDITS_STATE) {
            processCreditsState();
        }

    }

    return 0;
}
